    draw(E--(-1,4), gray);
    dot("$a$", A, -dir(B-A), deepgreen);
    dot("$b$", B, -dir(C-B), deepgreen);
    dot("$c$", C, -dir(D-C), deepgreen);
    dot("$d$", D, -dir(E-D), deepgreen);
    dot("$e$", E, dir(90), deepgreen);
    draw( (1,0.2)..(1.4,1)..(1,1.8), red+1.8, EndArrow(TeXHead));
