size(12.5cm);

pair A,E,O_A,O_B,T;
T = (0,0);
A = (14.4,-6);
O_A = (16.9,0);
E = 1.6 * A;
O_B = 1.6 * O_A;

draw(circle(O_A,abs(O_A-A)),gray);
draw(circle(O_B,abs(O_B-E)),gray);
pair B = intersectionpoint(circumcircle(T,E,O_B),circle(O_B,abs(O_B-E)));
draw(T--1.2*E);
draw(T--1.2*B);

pair G = (17,-3);
// draw(circumcircle(A,B,G),red);

