filldraw(circumcircle(B,C,F), opacity(0.1)+yellow, red);
pair V = extension(T,C,X,E);
pair W = extension(T,D,X,F);

draw(A--C,red);
draw(B--D,red);
// draw(-0.2*X--1.6*X,dashed);
draw(A--Y--B, gray+dotted);
