  dot("$\frac{ab}{c}$", C, dir(50), blue);
  dot("$H_1^C$", 2*A-B, dir(A-B));
  dot("$H_2^C$", 2*B-A, dir(B-A));
  dot("$H_2^A$", 2*B-C, dir(B-C));
  dot("$H_3^A$", 2*C-B, dir(C-B));
  dot("$H_3^B$", 2*C-A, dir(C-A));
  dot("$H_1^B$", 2*A-C, dir(A-C));
