pair S = (-3.81946,7.27786);
pair Pp = (-7.60181,6.33228);
pair Op = (-4.49671,1.94937);
fill(T--S--P--cycle, palegreen);
fill(T--Op--Y--cycle, palegreen);
fill(T--S--Pp--cycle, palecyan);
