pair M_b = dir(210);
pair M_c = dir(330);
pair A = M_b+M_c-M_a;
pair B = M_c+M_a-M_b;
pair C = M_a+M_b-M_c;
pair O = circumcenter(A, B, C);
pair S = 0.75*O;
