fill((0,0)--(2,0)--(2,4)--(4,4)--(4,8)--(0,8)--cycle, cyan+opacity(0.2));
fill((2,0)--(6,0)--(6,2)--(4,2)--(4,4)--(2,4)--cycle, green+opacity(0.2));
