fill((4,2)--(6,2)--(6,0)--(8,0)--(8,8)--(6,8)--(6,4)--(4,4)--cycle, yellow+opacity(0.2));

for (int i=1; i<=4; ++i) {
  draw((0,2*i-1)--(8,2*i-1), lightgray+1);
  draw((2*i-1,0)--(2*i-1,8), lightgray+1);
}
for (int i=0; i<=4; ++i) {
  draw((0,2*i)--(8,2*i), gray+1.5+linetype("3 2"));
  draw((2*i,0)--(2*i,8), gray+1.5+linetype("3 2"));
}

path red_path = (0,0)--(2,0)--(2,4)--(6,4)--(6,8)--(8,8);
path blue_path = (0,8)--(4,8)--(4,2)--(6,2)--(6,0)--(8,0);
