pair P_1 = ( 0,-3);
pair P_2 = (2.5,-3);
pair P_3 = (3,0);
pair P_4 = (1.8,1.2);
pair P_5 = (1.1,1.9);
pair P_6 = (0.3,2.7);
pair P_7 = (-2,0.4);
filldraw(P_0--P_2--P_3--P_6--P_7--cycle, opacity(0.1)+lightcyan, blue);

filldraw(P_0--P_4--P_5--P_1--cycle, opacity(0.2)+lightred, red);
draw(P_1--P_5, red);
draw(P_2--P_6, red);
draw(P_3--P_7, red);

dot("$P_0$", P_0, dir(P_0));
dot("$P_1$", P_1, dir(P_1));
dot("$P_2$", P_2, dir(P_2));
dot("$P_3$", P_3, dir(P_3));
dot("$P_4$", P_4, dir(P_4));
dot("$P_5$", P_5, dir(P_5));
dot("$P_6$", P_6, dir(P_6));
dot("$P_7$", P_7, dir(P_7));

